import React from 'react';
import './index.css';
import {useFormik} from 'formik';

function App() {
  // TODO: add a const called formik assigned to useFormik()
  const formik = useFormik({
    initialValues: {
      email: '',
      password: ''
    },
    onSubmit: values => {
      console.log('form:', values );
    },
    validate: values => {
      let errors = {};
      if(!values.email) errors.email = 'Field required';
      if(!values.password) {
        errors.password = 'Field required';
      }else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
        errors.password = 'Username should be an email';
      }
      return errors;
    }

  });

  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <div>Email</div>
        <input id="emailField" name="email" type="text" onChange={formik.handleChange} value={formik.values.email}></input>
        {formik.errors.email ? <div id='emailError' style={{color:'red'}}>{formik.errors.email}</div>: null}
        <div>Password</div>
        <input id='pswField' name="password" type="text" onChange={formik.handleChange} value={formik.values.password}></input>
        {formik.errors.password ? <div id="pswError" style={{color:'red'}}>{formik.errors.password}</div>: null}
        <button id='submitBtn' type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
